def assemble(program):
    opcodes = {
        'CONTROL': 0,
        'COND': 128,
        'LOAD': 4,
        'SWP': 8,
        'PUSH': 32,
        'POP': 33
    }
    def encode_line(line):
        opcode, argument, _ = line.split()
        command_byte = opcodes[opcode]
        argument_byte = int(argument) & 0xFF
        return command_byte + argument_byte
    return list(map(encode_line, program))
