        for i, v in enumerate(obj):
            new_key = f"{parent_key}{sep}{i}" if parent_key else str(i)
            items.extend(flatten(v, new_key, sep=sep).items())
    # If obj is a primitive type (int, float, string), return it as-is
    else:
        return {parent_key: obj}

    return dict(items)
